import React,{useState,useEffect} from "react";
import axios from "axios";

import { Dialog } from "@reach/dialog";
import "@reach/dialog/styles.css";
import CreateForm from "./CreateForm";
import UpdateForm from './UpdateForm'



function GetData(loginFormData) {
  const [isOpen, setIsOpen] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
 
    
  const [persons, setPersons] = useState([]);
 
  const handleAddClick = () => { setIsOpen(true) };
  const handleClick = () => { setModalOpen(true) }
  const handleCloseDialog = () => { setIsOpen(false) }
  
    
    
  const getData = () => {
    axios.get("https://gorest.co.in/public/v1/users", {
      headers: {
        "Content-Type": "application/json",
        "Authorization": 'Bearer bc1e0809f9bb5ce03125ea49290ec9c8acc225870ebd21e484217e79b88800db'
      }
    })
      .then(response => setPersons(response.data.data))
  }
  function reloadP() {

        
        window.location.reload(false)
        console.log("reloading")
    
    
  }
 

  useEffect(() => {
    getData() 
    
  }, []);
    
 

   

  return (
    <div>
      
      <table className="table table-striped" >
        <thead>
          <tr>
            <th> ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Gender</th>
            <th>Status</th>
          </tr>
        </thead>
        
        <tbody>
         
          {(persons).map((person,row) => {
            return (
              
              <tr key={person.id} onClick={handleClick}>
                <td>{person.id}</td>
                <td>{person.name}</td>
                <td>{person.email}</td>
                <td>{person.gender}</td>
                <td>{person.status}</td>
              </tr>)
          })}
            
        </tbody>
          
      </table>
      <i className="fa fa-plus-circle" style={{ fontSize: '60px', position: 'absolute', right: '10px', bottom: '10px', position: 'fixed' }} onClick={handleAddClick} />
       
      <div >
        <Dialog aria-label="Update Form" isOpen={isOpen} style={{ width: '30%', borderRadius: '10px' }} onDismiss={() => setIsOpen(false)} onSubmit={handleCloseDialog}>
          <CreateForm />
        </Dialog>
        <Dialog aria-label="Update Form" modalOpen={modalOpen} style={{ width: '30%', borderRadius: '10px' }} onDismiss={() => setModalOpen(false)} >
        <UpdateForm />
            </Dialog>
      </div>
        
   
        
          
       </div>
  );
 
}


export default GetData
