import React,{useState} from 'react'
import { Dialog } from "@reach/dialog";
import axios from "axios";

function CreateForm() {
    const [person, setPerson] = useState([]);
    const[hide,setHide]=useState(false)
  
    let handleSubmit = async (e) => {
        
        e.preventDefault();
        
        var data = JSON.stringify({
            "name": person.name.value,
            "email": person.email.value,
            "gender": person.gender.value,
            "status": person.status.value
          });
          
          var config = {
            method: 'post',
            url: 'https://gorest.co.in/public/v1/users',
            headers: { 
              'Authorization': 'Bearer bc1e0809f9bb5ce03125ea49290ec9c8acc225870ebd21e484217e79b88800db', 
              'Content-Type': 'application/json'
            },
            data : data
          };
          
          axios(config)
          .then(function (response) {
            console.log(JSON.stringify(response.data));
          })
          .catch(function (error) {
            console.log(error);
          });
          
      }
  return (
      <div>
          <div hide={hide}>
    <h2>Create User:-</h2>
<form  onSubmit={handleSubmit}>
 <p> Name: <input type="text" name={person.name} value={person.name} placeholder="Enter your name" required onChange={(e)=>setPerson(e.target.value)}/></p><br/>
        <p> Email: <input type="email" name={person.email} value={person.email}  placeholder="Enter your email" onChange={(e) => setPerson(e.target.value)}/></p><br/>
        <p> Gender: <input type="text" name={person.gender} value={person.gender}  placeholder="Enter your gender" onChange={(e) => setPerson(e.target.value)}/></p><br/>
        <p>Status: <select name="status" value={person.status} onChange={(e) => setPerson(e.target.value)} >
     <option value="active">Active</option>
     <option value="inactive">Inactive</option>
 </select></p><br/>
 <button id="btn" type="submit" >Create</button>
              </form>
              </div>     
</div>
  )
}

export default CreateForm
