import React,{useState,useEffect} from "react";
import axios from "axios";

import { Dialog } from "@reach/dialog";
import "@reach/dialog/styles.css";


function GetData() {
    const [isOpen, setIsOpen] = useState(false);
    const handleAddClick = () => {
       
        setIsOpen(true);
     
    
    
      };
      
    const [persons, setPersons] = useState([]);
    const getData = () => {
      axios.get("https://gorest.co.in/public/v1/users", {
        headers: {
          "Content-Type": "application/json",
          "Authorization": "Bearer bc1e0809f9bb5ce03125ea49290ec9c8acc225870ebd21e484217e79b88800db"
        }
      })
        .then(response => setPersons(response.data.data))
    }

    useEffect(() => {
      getData()
    
    }, []);
    


    return (
      <div>
      
        <table className="table table-striped">
          <thead>
            <tr>
              <th> ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Gender</th>
              <th>Status</th>
            </tr>
          </thead>
        
          <tbody>
         
            {(persons).map((person) => {
              return (
                <tr key={person.id}>
                  <td>{person.id}</td>
                  <td>{person.name}</td>
                  <td>{person.email}</td>
                  <td>{person.gender}</td>
                  <td>{person.status}</td>
                </tr>)
            })}
            
          </tbody>
          
        </table>
            <i className="fa fa-plus-circle" style={{ fontSize: '60px', position: 'absolute', right: '10px', bottom: '10px', position: 'fixed' }} onClick={handleAddClick} />
        
            <Dialog isOpen={isOpen} style={{width:'50px'}}>
                <h2>Create User:-</h2>
         <form  >
             <p> Name: <input type="text" name="" id="name" placeholder="Enter your name" required/></p><br/>
             <p> Email: <input type="email" name="" id="email" placeholder="Enter your email"/></p><br/>
             <p> Gender: <input type="text" name="" id="gender" placeholder="Enter your gender"/></p><br/>
             <p>Status: <select name="status" id="status">
                 <option value="active">Active</option>
                 <option value="inactive">Inactive</option>
             </select></p><br/>
             <button id="btn" type="submit">Create</button>
        </form>
            </Dialog>
            </div>
    );
 
}

export default GetData
