import React,{useState} from 'react'
import { Dialog } from "@reach/dialog";
import axios from "axios";

function CreateForm() {
    const [person, setPerson] = useState([]);
    const[hide,setHide]=useState(false)
  
    let handleSubmit = async (e) => {
        
        e.preventDefault();
        
        axios.post("https://gorest.co.in/public/v1/users", {
          body: JSON.stringify({
            name: person.name,
            email: person.email,
            gender: person.gender,
            status: person.status
          }),
          headers: {
            "Content-Type": "application/json",
            "Authorization": "Bearer bc1e0809f9bb5ce03125ea49290ec9c8acc225870ebd21e484217e79b88800db"
          }
        })
          .then(response => console.log(response.data.data))
          setHide(true)
      }
  return (
      <div>
          <div hide={hide}>
    <h2>Create User:-</h2>
<form  onSubmit={handleSubmit}>
 <p> Name: <input type="text" name={person.name}  placeholder="Enter your name" required onChange={(e)=>setPerson(e.target.value)}/></p><br/>
        <p> Email: <input type="email" name={person.email}   placeholder="Enter your email" onChange={(e) => setPerson(e.target.value)}/></p><br/>
        <p> Gender: <input type="text" name={person.gender}   placeholder="Enter your gender" onChange={(e) => setPerson(e.target.value)}/></p><br/>
        <p>Status: <select name="status"  onChange={(e) => setPerson(e.target.value)} >
     <option value="active">Active</option>
     <option value="inactive">Inactive</option>
 </select></p><br/>
 <button id="btn" type="submit" >Create</button>
              </form>
              </div>     
</div>
  )
}

export default CreateForm
